import {createSlice} from '@reduxjs/toolkit';


const initialState = {
    cartItems:{
        name:null,
        qty:null,
        actualPrice:null,
        discountedPrice:null,
        percentageDiscount:null
    },
    totalItems:null

}
const cartSlice = createSlice({
    name:'cart',
    initialState,
    extraReducers:{
        ['cake/ordered']:(state,action) => {
            state.totalItems += action.payload;
        }
    }
})


export default cartSlice.reducer;
